import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import './components.css';
import Header from './components/Header';
import Hero from './components/Hero';
import Features from './components/Features';
import Login from './components/Login';
import ProfileInput from './components/ProfileInput';
import SchemeMatcher from './components/SchemeMatcher';
import FakeNewsChecker from './components/FakeNewsChecker';
import ScholarshipResources from './components/ScholarshipResources';
import About from './components/About';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import DebugInfo from './components/DebugInfo';
import { LanguageProvider, useLanguage } from './context/LanguageContext';
import { AuthProvider, useAuth } from './context/AuthContext';

// Protected Route component
const ProtectedRoute = ({ children }) => {
  const { currentUser, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }
  
  if (!currentUser) {
    return <Navigate to="/login" />;
  }
  
  return children;
};

// Public Route component (only for non-authenticated users)
const PublicRoute = ({ children }) => {
  const { currentUser, hasProfile, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }
  
  if (currentUser) {
    // If user is logged in but doesn't have a profile, redirect to profile setup
    if (!hasProfile()) {
      return <Navigate to="/profile" />;
    }
    // If user has a complete profile, redirect to home
    return <Navigate to="/" />;
  }
  
  return children;
};

function AppContent() {
  const { currentUser, hasProfile, loading } = useAuth();

  // Debug logging
  console.log('Auth State:', { 
    currentUser: !!currentUser, 
    hasProfile: hasProfile(), 
    loading 
  });

  return (
    <div className="App">
      <DebugInfo />
      {/* Development debug info */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{ 
          position: 'fixed', 
          top: '80px', 
          right: '10px', 
          background: 'rgba(0,0,0,0.8)', 
          color: 'white', 
          padding: '10px', 
          borderRadius: '5px', 
          fontSize: '12px',
          zIndex: 1000
        }}>
          Auth: {currentUser ? 'Logged In' : 'Not Logged In'}<br/>
          Profile: {hasProfile() ? 'Complete' : 'Incomplete'}<br/>
          Loading: {loading ? 'Yes' : 'No'}<br/>
          Email: {currentUser?.email || 'None'}
        </div>
      )}
      
      <Header />
      <Routes>
        <Route path="/" element={
          <>
            <Hero />
            <Features />
          </>
        } />
        <Route path="/login" element={
          <PublicRoute>
            <Login />
          </PublicRoute>
        } />
        <Route path="/profile" element={
          <ProtectedRoute>
            <ProfileInput />
          </ProtectedRoute>
        } />
        <Route path="/schemes" element={
          <ProtectedRoute>
            <SchemeMatcher />
          </ProtectedRoute>
        } />
        <Route path="/fake-news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/scholarships" element={
          <ProtectedRoute>
            <ScholarshipResources />
          </ProtectedRoute>
        } />
        <Route path="/news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/about" element={
          <div className="about-page" style={{ 
            backgroundColor: '#f8f9fa', 
            minHeight: '100vh', 
            paddingTop: '80px',
            paddingBottom: '60px'
          }}>
            <div className="container" style={{ 
              maxWidth: '1200px', 
              margin: '0 auto', 
              padding: '0 20px' 
            }}>
              
              {/* Header Section */}
              <div style={{ 
                textAlign: 'center', 
                marginBottom: '60px',
                backgroundColor: 'white',
                padding: '60px 40px',
                borderRadius: '12px',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
              }}>
                <h1 style={{ 
                  fontSize: '2.5rem', 
                  fontWeight: '700', 
                  color: '#2d3748', 
                  marginBottom: '20px' 
                }}>About Smart Sarkari Sathi</h1>
                <p style={{ 
                  fontSize: '1.2rem', 
                  color: '#4a5568', 
                  lineHeight: '1.6',
                  maxWidth: '800px',
                  margin: '0 auto'
                }}>
                  Smart Sarkari Sathi is an AI-powered digital assistant designed to simplify access to government schemes, services, and information for every citizen of India.
                </p>
              </div>

              {/* Mission Section */}
              <div style={{
                backgroundColor: 'white',
                padding: '50px 40px',
                borderRadius: '12px',
                marginBottom: '30px',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
              }}>
                <h2 style={{ 
                  fontSize: '1.8rem', 
                  fontWeight: '600', 
                  color: '#2d3748', 
                  marginBottom: '20px' 
                }}>Our Mission</h2>
                <p style={{ 
                  fontSize: '1.1rem', 
                  color: '#4a5568', 
                  lineHeight: '1.7',
                  marginBottom: '0'
                }}>
                  To bridge the gap between people and the often complex, scattered world of public services by offering a single, easy-to-use platform. We aim to ensure equitable access to resources and opportunities for all citizens through innovative solutions and collaborative partnerships.
                </p>
              </div>

              {/* Features Section */}
              <div style={{
                backgroundColor: 'white',
                padding: '50px 40px',
                borderRadius: '12px',
                marginBottom: '30px',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
              }}>
                <h2 style={{ 
                  fontSize: '1.8rem', 
                  fontWeight: '600', 
                  color: '#2d3748', 
                  marginBottom: '30px' 
                }}>What We Offer</h2>
                
                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
                  gap: '25px' 
                }}>
                  <div style={{ padding: '20px 0' }}>
                    <h3 style={{ 
                      fontSize: '1.2rem', 
                      fontWeight: '600', 
                      color: '#c44569', 
                      marginBottom: '10px' 
                    }}>üîç Search & Discover</h3>
                    <p style={{ color: '#4a5568', lineHeight: '1.6' }}>
                      Government schemes relevant to your profile, location, and eligibility
                    </p>
                  </div>
                  
                  <div style={{ padding: '20px 0' }}>
                    <h3 style={{ 
                      fontSize: '1.2rem', 
                      fontWeight: '600', 
                      color: '#c44569', 
                      marginBottom: '10px' 
                    }}>üéØ Personalized Recommendations</h3>
                    <p style={{ color: '#4a5568', lineHeight: '1.6' }}>
                      Based on age, profession, income, and other factors
                    </p>
                  </div>
                  
                  
                  
                  <div style={{ padding: '20px 0' }}>
                    <h3 style={{ 
                      fontSize: '1.2rem', 
                      fontWeight: '600', 
                      color: '#c44569', 
                      marginBottom: '10px' 
                    }}>üåê Multilingual Support</h3>
                    <p style={{ color: '#4a5568', lineHeight: '1.6' }}>
                      Inclusivity across India's diverse population
                    </p>
                  </div>
                  
                  
                  
                  <div style={{ padding: '20px 0' }}>
                    <h3 style={{ 
                      fontSize: '1.2rem', 
                      fontWeight: '600', 
                      color: '#c44569', 
                      marginBottom: '10px' 
                    }}>ü§ñ AI-Powered</h3>
                    <p style={{ color: '#4a5568', lineHeight: '1.6' }}>
                      Machine learning for accurate, timely services
                    </p>
                  </div>
                </div>
              </div>

              {/* Vision Section */}
              <div style={{
                backgroundColor: 'white',
                padding: '50px 40px',
                borderRadius: '12px',
                marginBottom: '30px',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
              }}>
                <h2 style={{ 
                  fontSize: '1.8rem', 
                  fontWeight: '600', 
                  color: '#2d3748', 
                  marginBottom: '20px' 
                }}>Our Vision</h2>
                <p style={{ 
                  fontSize: '1.1rem', 
                  color: '#4a5568', 
                  lineHeight: '1.7',
                  marginBottom: '20px'
                }}>
                  We envision a future where no citizen misses out on the opportunities and support they deserve. Our platform integrates AI, machine learning, and government data APIs to provide accurate, timely, and citizen-friendly services.
                </p>
                <p style={{ 
                  fontSize: '1.1rem', 
                  color: '#2d3748', 
                  lineHeight: '1.7',
                  fontWeight: '600'
                }}>
                  Smart Sarkari Sathi is the first step toward that goal ‚Äî acting as your trusted companion in navigating the public service ecosystem.
                </p>
              </div>
            </div>
          </div>
        } />
        
        {/* Admin Routes */}
        <Route path="/admin/login" element={<AdminLogin />} />
        <Route path="/admin/dashboard" element={<AdminDashboard />} />
        
        {/* Catch all route */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </div>
  );
}

function App() {
  return (
    <LanguageProvider>
      <AuthProvider>
        <Router>
          <AppContent />
        </Router>
      </AuthProvider>
    </LanguageProvider>
  );
}

export default App;
