import React, { useState } from 'react';
import { AlertTriangle, CheckCircle, XCircle, Loader, FileText, Copy, Share2 } from 'lucide-react';

const FakeNewsChecker = () => {
  const [inputText, setInputText] = useState('');
  const [url, setUrl] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('text');

  const mockAnalysis = {
    isFake: false,
    confidence: 87,
    reasons: [
      "Information matches official government sources",
      "Claims are realistic and verifiable",
      "No sensational language detected",
      "Source appears credible"
    ],
    recommendations: [
      "This appears to be authentic information",
      "You can proceed with confidence",
      "Always verify through official channels"
    ],
    officialSources: [
      "Official government website",
      "Ministry of Finance",
      "PMO India"
    ]
  };

  const handleAnalyze = async () => {
    if (!inputText.trim() && !url.trim()) {
      alert('Please enter some text or URL to analyze');
      return;
    }

    setLoading(true);
    
    // Simulate AI analysis
    setTimeout(() => {
      setResult(mockAnalysis);
      setLoading(false);
    }, 2000);
  };

  const handleClear = () => {
    setInputText('');
    setUrl('');
    setResult(null);
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    alert('Copied to clipboard!');
  };

  const shareResult = () => {
    if (navigator.share) {
      navigator.share({
        title: 'Fake News Check Result',
        text: `Analysis result: ${result.isFake ? 'FAKE' : 'AUTHENTIC'} news with ${result.confidence}% confidence`,
        url: window.location.href
      });
    } else {
      copyToClipboard(`Analysis result: ${result.isFake ? 'FAKE' : 'AUTHENTIC'} news with ${result.confidence}% confidence`);
    }
  };

  return (
    <div className="fake-news-checker">
      <div className="checker-container">
        <div className="checker-header">
          <AlertTriangle className="checker-icon" />
          <h1>Fake News Checker</h1>
          <p>Detect misleading or fake information about government schemes and scholarships</p>
        </div>

        <div className="checker-tabs">
          <button 
            className={`tab-btn ${activeTab === 'text' ? 'active' : ''}`}
            onClick={() => setActiveTab('text')}
          >
            <FileText className="tab-icon" />
            Text Analysis
          </button>
          <button 
            className={`tab-btn ${activeTab === 'url' ? 'active' : ''}`}
            onClick={() => setActiveTab('url')}
          >
            <Share2 className="tab-icon" />
            URL Analysis
          </button>
        </div>

        <div className="checker-input">
          {activeTab === 'text' ? (
            <div className="text-input-section">
              <label>Enter the news text or claim to analyze:</label>
              <textarea
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                placeholder="Paste the news text, social media post, or claim here..."
                rows={6}
              />
            </div>
          ) : (
            <div className="url-input-section">
              <label>Enter the URL to analyze:</label>
              <input
                type="url"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="https://example.com/news-article"
              />
            </div>
          )}

          <div className="input-actions">
            <button 
              className="analyze-btn" 
              onClick={handleAnalyze}
              disabled={loading || (!inputText.trim() && !url.trim())}
            >
              {loading ? (
                <>
                  <Loader className="spinner" />
                  Analyzing...
                </>
              ) : (
                <>
                  <AlertTriangle className="analyze-icon" />
                  Analyze for Fake News
                </>
              )}
            </button>
            <button className="clear-btn" onClick={handleClear}>
              Clear
            </button>
          </div>
        </div>

        {result && (
          <div className="analysis-result">
            <div className={`result-header ${result.isFake ? 'fake' : 'authentic'}`}>
              <div className="result-icon">
                {result.isFake ? <XCircle className="fake-icon" /> : <CheckCircle className="authentic-icon" />}
              </div>
              <div className="result-summary">
                <h2>{result.isFake ? 'FAKE NEWS DETECTED' : 'AUTHENTIC INFORMATION'}</h2>
                <p>Confidence: {result.confidence}%</p>
              </div>
              <button className="share-btn" onClick={shareResult}>
                <Share2 className="share-icon" />
                Share
              </button>
            </div>

            <div className="result-details">
              <div className="result-section">
                <h3>Analysis Reasons</h3>
                <ul>
                  {result.reasons.map((reason, index) => (
                    <li key={index}>{reason}</li>
                  ))}
                </ul>
              </div>

              <div className="result-section">
                <h3>Recommendations</h3>
                <ul>
                  {result.recommendations.map((rec, index) => (
                    <li key={index}>{rec}</li>
                  ))}
                </ul>
              </div>

              <div className="result-section">
                <h3>Official Sources to Verify</h3>
                <div className="sources-list">
                  {result.officialSources.map((source, index) => (
                    <div key={index} className="source-item">
                      <span>{source}</span>
                      <button 
                        className="copy-btn"
                        onClick={() => copyToClipboard(source)}
                      >
                        <Copy className="copy-icon" />
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        )}

        <div className="checker-info">
          <h3>How it works</h3>
          <div className="info-grid">
            <div className="info-item">
              <h4>AI Analysis</h4>
              <p>Our specialized AI analyzes text patterns, sources, and claims against verified government data.</p>
            </div>
            <div className="info-item">
              <h4>Source Verification</h4>
              <p>Cross-references information with official government websites and databases.</p>
            </div>
            <div className="info-item">
              <h4>Pattern Recognition</h4>
              <p>Identifies common fake news patterns and sensational language.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FakeNewsChecker;
