import React, { useEffect, useState } from 'react';

const DebugInfo = () => {
  const [apiUrl, setApiUrl] = useState('');
  const [backendStatus, setBackendStatus] = useState('checking...');

  useEffect(() => {
    setApiUrl(process.env.REACT_APP_API_URL || 'Not set');
    
    // Test backend connection
    const testBackend = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}/api/health`);
        if (response.ok) {
          setBackendStatus('✅ Connected');
        } else {
          setBackendStatus(`❌ Error: ${response.status}`);
        }
      } catch (error) {
        setBackendStatus(`❌ Failed: ${error.message}`);
      }
    };

    testBackend();
  }, []);

  // Only show in development
  if (process.env.NODE_ENV !== 'development') {
    return null;
  }

  return (
    <div style={{
      position: 'fixed',
      bottom: '10px',
      left: '10px',
      background: 'rgba(0,0,0,0.8)',
      color: 'white',
      padding: '10px',
      borderRadius: '5px',
      fontSize: '12px',
      zIndex: 9999
    }}>
      <div><strong>Debug Info:</strong></div>
      <div>API URL: {apiUrl}</div>
      <div>Backend: {backendStatus}</div>
    </div>
  );
};

export default DebugInfo;
