import React, { useState } from 'react';
import { Calendar, FileText, CheckCircle, Clock, AlertCircle, ExternalLink, Plus } from 'lucide-react';

const ApplicationTracker = () => {
  const [applications, setApplications] = useState([
    {
      id: 1,
      schemeName: "PM Kisan Samman Nidhi",
      applicationId: "PMKSN2024001",
      status: "approved",
      appliedDate: "2024-01-15",
      lastUpdated: "2024-02-01",
      expectedCompletion: "2024-03-15",
      documents: [
        { name: "Aadhaar Card", status: "verified" },
        { name: "Land Records", status: "verified" },
        { name: "Bank Details", status: "pending" }
      ],
      notes: "Application approved, waiting for bank verification",
      officialLink: "https://pmkisan.gov.in"
    },
    {
      id: 2,
      schemeName: "PM Ujjwala Yojana",
      applicationId: "PMUY2024002",
      status: "pending",
      appliedDate: "2024-01-20",
      lastUpdated: "2024-01-25",
      expectedCompletion: "2024-04-20",
      documents: [
        { name: "Aadhaar Card", status: "verified" },
        { name: "BPL Certificate", status: "pending" },
        { name: "Address Proof", status: "verified" }
      ],
      notes: "BPL certificate verification in progress",
      officialLink: "https://pmuy.gov.in"
    },
    {
      id: 3,
      schemeName: "PM Awas Yojana",
      applicationId: "PMAY2024003",
      status: "rejected",
      appliedDate: "2024-01-10",
      lastUpdated: "2024-01-30",
      expectedCompletion: null,
      documents: [
        { name: "Income Certificate", status: "rejected" },
        { name: "Caste Certificate", status: "verified" },
        { name: "Land Documents", status: "verified" }
      ],
      notes: "Income certificate not meeting criteria",
      officialLink: "https://pmay.gov.in"
    }
  ]);

  const [showAddForm, setShowAddForm] = useState(false);
  const [newApplication, setNewApplication] = useState({
    schemeName: '',
    applicationId: '',
    appliedDate: '',
    officialLink: ''
  });

  const getStatusIcon = (status) => {
    switch (status) {
      case 'approved':
        return <CheckCircle className="status-icon approved" />;
      case 'pending':
        return <Clock className="status-icon pending" />;
      case 'rejected':
        return <AlertCircle className="status-icon rejected" />;
      default:
        return <Clock className="status-icon pending" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved':
        return 'approved';
      case 'pending':
        return 'pending';
      case 'rejected':
        return 'rejected';
      default:
        return 'pending';
    }
  };

  const getDocumentStatusIcon = (status) => {
    switch (status) {
      case 'verified':
        return <CheckCircle className="doc-icon verified" />;
      case 'pending':
        return <Clock className="doc-icon pending" />;
      case 'rejected':
        return <AlertCircle className="doc-icon rejected" />;
      default:
        return <Clock className="doc-icon pending" />;
    }
  };

  const handleAddApplication = (e) => {
    e.preventDefault();
    const application = {
      id: applications.length + 1,
      ...newApplication,
      status: 'pending',
      lastUpdated: new Date().toISOString().split('T')[0],
      documents: [],
      notes: 'Application submitted successfully'
    };
    setApplications([...applications, application]);
    setNewApplication({ schemeName: '', applicationId: '', appliedDate: '', officialLink: '' });
    setShowAddForm(false);
  };

  return (
    <div className="application-tracker">
      <div className="tracker-container">
        <div className="tracker-header">
          <MapPin className="tracker-icon" />
          <h1>Application Tracker</h1>
          <p>Track your scheme applications and their current status</p>
        </div>

        <div className="tracker-actions">
          <button 
            className="add-application-btn"
            onClick={() => setShowAddForm(true)}
          >
            <Plus className="add-icon" />
            Add New Application
          </button>
        </div>

        {showAddForm && (
          <div className="add-application-form">
            <h3>Add New Application</h3>
            <form onSubmit={handleAddApplication}>
              <div className="form-row">
                <div className="form-group">
                  <label>Scheme Name</label>
                  <input
                    type="text"
                    value={newApplication.schemeName}
                    onChange={(e) => setNewApplication({...newApplication, schemeName: e.target.value})}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Application ID</label>
                  <input
                    type="text"
                    value={newApplication.applicationId}
                    onChange={(e) => setNewApplication({...newApplication, applicationId: e.target.value})}
                    required
                  />
                </div>
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>Applied Date</label>
                  <input
                    type="date"
                    value={newApplication.appliedDate}
                    onChange={(e) => setNewApplication({...newApplication, appliedDate: e.target.value})}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Official Link</label>
                  <input
                    type="url"
                    value={newApplication.officialLink}
                    onChange={(e) => setNewApplication({...newApplication, officialLink: e.target.value})}
                    placeholder="https://example.com"
                  />
                </div>
              </div>
              <div className="form-actions">
                <button type="submit" className="submit-btn">Add Application</button>
                <button 
                  type="button" 
                  className="cancel-btn"
                  onClick={() => setShowAddForm(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        <div className="applications-list">
          {applications.map((application) => (
            <div key={application.id} className="application-card">
              <div className="application-header">
                <div className="application-info">
                  <h3>{application.schemeName}</h3>
                  <p className="application-id">ID: {application.applicationId}</p>
                </div>
                <div className={`status-badge ${getStatusColor(application.status)}`}>
                  {getStatusIcon(application.status)}
                  <span>{application.status.toUpperCase()}</span>
                </div>
              </div>

              <div className="application-details">
                <div className="detail-row">
                  <div className="detail-item">
                    <Calendar className="detail-icon" />
                    <span>Applied: {application.appliedDate}</span>
                  </div>
                  <div className="detail-item">
                    <Calendar className="detail-icon" />
                    <span>Updated: {application.lastUpdated}</span>
                  </div>
                  {application.expectedCompletion && (
                    <div className="detail-item">
                      <Calendar className="detail-icon" />
                      <span>Expected: {application.expectedCompletion}</span>
                    </div>
                  )}
                </div>

                <div className="documents-section">
                  <h4>Document Status</h4>
                  <div className="documents-list">
                    {application.documents.map((doc, index) => (
                      <div key={index} className="document-item">
                        {getDocumentStatusIcon(doc.status)}
                        <span>{doc.name}</span>
                        <span className={`doc-status ${doc.status}`}>{doc.status}</span>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="notes-section">
                  <h4>Notes</h4>
                  <p>{application.notes}</p>
                </div>

                <div className="application-actions">
                  <a 
                    href={application.officialLink} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="official-link-btn"
                  >
                    <ExternalLink className="link-icon" />
                    Official Website
                  </a>
                  <button className="update-btn">Update Status</button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {applications.length === 0 && (
          <div className="empty-state">
            <MapPin className="empty-icon" />
            <h3>No Applications Yet</h3>
            <p>Start by adding your first scheme application to track its progress.</p>
            <button 
              className="add-first-btn"
              onClick={() => setShowAddForm(true)}
            >
              Add Your First Application
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ApplicationTracker;
