import React from 'react';
import { Link } from 'react-router-dom';
import { useLanguage } from '../context/LanguageContext';
import { Menu, AlertTriangle, MapPin, GraduationCap } from 'lucide-react';

const Features = () => {
  const { t } = useLanguage();

  const features = [
    {
      id: 'scheme-recommender',
      icon: <Menu className="feature-icon" />,
      title: t('schemeRecommender'),
      description: t('schemeRecommenderDesc'),
      link: '/schemes',
      color: 'blue'
    },
    {
      id: 'fake-news-checker',
      icon: <AlertTriangle className="feature-icon" />,
      title: t('fakeNewsChecker'),
      description: t('fakeNewsCheckerDesc'),
      link: '/fake-news',
      color: 'orange'
    },
    
    {
      id: 'scholarship-resources',
      icon: <GraduationCap className="feature-icon" />,
      title: t('scholarshipResources'),
      description: t('scholarshipResourcesDesc'),
      link: '/scholarships',
      color: 'purple'
    }
  ];

  return (
    <section className="features" id="features">
      <div className="features-container">
        <div className="features-grid">
          {features.map((feature) => (
            <Link to={feature.link} key={feature.id} className="feature-card">
              <div className={`feature-icon-container ${feature.color}`}>
                {feature.icon}
              </div>
              <h3 className="feature-title">{feature.title}</h3>
              <p className="feature-description">{feature.description}</p>
            </Link>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Features;
