import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Hero from './components/Hero';
import Features from './components/Features';
import Login from './components/Login';
import ProfileInput from './components/ProfileInput';
import SchemeMatcher from './components/SchemeMatcher';
import FakeNewsChecker from './components/FakeNewsChecker';
import ScholarshipResources from './components/ScholarshipResources';
import About from './components/About';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import { LanguageProvider, useLanguage } from './context/LanguageContext';
import { AuthProvider, useAuth } from './context/AuthContext';

// Protected Route component
const ProtectedRoute = ({ children }) => {
  const { currentUser, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }
  
  if (!currentUser) {
    return <Navigate to="/login" />;
  }
  
  return children;
};

// Public Route component (only for non-authenticated users)
const PublicRoute = ({ children }) => {
  const { currentUser, hasProfile, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }
  
  if (currentUser) {
    // If user is logged in but doesn't have a profile, redirect to profile setup
    if (!hasProfile()) {
      return <Navigate to="/profile" />;
    }
    // If user has a complete profile, redirect to home
    return <Navigate to="/" />;
  }
  
  return children;
};

function AppContent() {
  const { currentUser, hasProfile, loading } = useAuth();

  return (
    <div className="App">
      <Header />
      <Routes>
        <Route path="/" element={
          <>
            <Hero />
            <Features />
          </>
        } />
        <Route path="/login" element={
          <PublicRoute>
            <Login />
          </PublicRoute>
        } />
        <Route path="/profile" element={
          <ProtectedRoute>
            <ProfileInput />
          </ProtectedRoute>
        } />
        <Route path="/schemes" element={
          <ProtectedRoute>
            <SchemeMatcher />
          </ProtectedRoute>
        } />
        <Route path="/fake-news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/scholarships" element={
          <ProtectedRoute>
            <ScholarshipResources />
          </ProtectedRoute>
        } />
        <Route path="/news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/about" element={
          <div className="min-h-screen bg-gray-50 pt-20 pb-16">
            <div className="max-w-6xl mx-auto px-5">
              
              {/* Header Section */}
              <div className="text-center mb-16 bg-white p-16 rounded-2xl shadow-lg">
                <h1 className="text-5xl font-bold text-gray-800 mb-5">
                  About Smart Sarkari Sathi
                </h1>
                <p className="text-xl text-gray-600 leading-relaxed max-w-4xl mx-auto">
                  Smart Sarkari Sathi is an AI-powered digital assistant designed to simplify access to government schemes, services, and information for every citizen of India.
                </p>
              </div>

              {/* Mission Section */}
              <div className="bg-white p-12 rounded-2xl mb-8 shadow-lg">
                <h2 className="text-3xl font-semibold text-gray-800 mb-5">Our Mission</h2>
                <p className="text-lg text-gray-600 leading-relaxed">
                  To bridge the gap between people and the often complex, scattered world of public services by offering a single, easy-to-use platform. We aim to ensure equitable access to resources and opportunities for all citizens through innovative solutions and collaborative partnerships.
                </p>
              </div>

              {/* Features Section */}
              <div className="bg-white p-12 rounded-2xl mb-8 shadow-lg">
                <h2 className="text-3xl font-semibold text-gray-800 mb-8">What We Offer</h2>
                
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="py-5">
                    <h3 className="text-xl font-semibold text-pink-600 mb-3">üîç Search & Discover</h3>
                    <p className="text-gray-600 leading-relaxed">
                      Government schemes relevant to your profile, location, and eligibility
                    </p>
                  </div>
                  
                  <div className="py-5">
                    <h3 className="text-xl font-semibold text-pink-600 mb-3">üéØ Personalized Recommendations</h3>
                    <p className="text-gray-600 leading-relaxed">
                      Based on age, profession, income, and other factors
                    </p>
                  </div>
                  
