import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { CheckCircle, ExternalLink, Star, Calendar, DollarSign, FileText, Award, MapPin, User, Video, AlertCircle, Loader, Play } from 'lucide-react';

const SchemeMatcher = () => {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedScheme, setSelectedScheme] = useState(null);
  const { currentUser } = useAuth();

  useEffect(() => {
    loadSchemes();
  }, [currentUser]);

  const loadSchemes = async () => {
    try {
      setLoading(true);
      setError('');

      // Build URL with user ID for personalized schemes
      let url = `${process.env.REACT_APP_API_URL}/api/schemes?type=scheme`;
      if (currentUser?.id) {
        url += `&userId=${currentUser.id}`;
      }

      console.log('🔍 Fetching schemes from:', url);
      console.log('👤 Current user:', currentUser);

      const response = await fetch(url);
      
      console.log('📡 Response status:', response.status);
      
      if (response.ok) {
        const data = await response.json();
        console.log('📊 Received data:', data);
        console.log('📝 Schemes array:', data.schemes);
        console.log('🔢 Number of schemes:', data.schemes?.length || 0);
        setSchemes(data.schemes || []);
      } else {
        throw new Error('Failed to load schemes');
      }
    } catch (error) {
      console.error('❌ Error loading schemes:', error);
      setError('Unable to load schemes. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleSchemeClick = (scheme) => {
    setSelectedScheme(scheme);
  };

  const closeModal = () => {
    setSelectedScheme(null);
  };

  const formatEligibilityArray = (eligibility) => {
    if (Array.isArray(eligibility.category)) {
      return eligibility.category;
    }
    return [];
  };

  if (loading) {
    return (
      <div className="scheme-matcher">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Finding the best schemes for you...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="scheme-matcher">
        <div className="error-container">
          <AlertCircle className="error-icon" />
          <h2>Unable to Load Schemes</h2>
          <p>{error}</p>
          <button onClick={loadSchemes} className="retry-btn">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="scheme-matcher">
      <div className="scheme-container">
        <div className="scheme-header">
          <h1>Government Schemes</h1>
          {schemes.length > 0 && currentUser?.profile ? (
            <p>Based on your profile, here are the schemes available for you</p>
          ) : (
            <p>Explore government schemes and benefits</p>
          )}
        </div>

        {schemes.length === 0 ? (
          <div className="no-schemes">
            <Award className="no-schemes-icon" />
            <h3>No Schemes Available</h3>
            <p>There are currently no schemes in the database.</p>
            <p>Check back later or contact the administrator.</p>
          </div>
        ) : (
          <div className="schemes-grid">
            {schemes.map((scheme) => (
              <div key={scheme._id} className="scheme-card" onClick={() => handleSchemeClick(scheme)}>
                <div className="scheme-header-card">
                  <div className="scheme-category">{scheme.type}</div>
                  {scheme.isActive && (
                    <div className="scheme-status active">Active</div>
                  )}
                </div>
                
                <h3 className="scheme-name">{scheme.title}</h3>
                <p className="scheme-description">{scheme.description}</p>
                
                <div className="scheme-benefits">
                  <DollarSign className="benefit-icon" />
                  <span>{scheme.benefits}</span>
                </div>
                
                {scheme.lastDate && (
                  <div className="scheme-deadline">
                    <Calendar className="deadline-icon" />
                    <span>Deadline: {new Date(scheme.lastDate).toLocaleDateString()}</span>
                  </div>
                )}
                
                <button className="view-details-btn">View Details</button>
              </div>
            ))}
          </div>
        )}

        {/* Scheme Detail Modal */}
        {selectedScheme && (
          <div className="scheme-modal-overlay" onClick={closeModal}>
            <div className="scheme-modal" onClick={(e) => e.stopPropagation()}>
              <div className="modal-header">
                <h2>{selectedScheme.title}</h2>
                <button className="close-modal" onClick={closeModal}>×</button>
              </div>
              
              <div className="modal-content">
                <div className="scheme-info-section">
                  <h3>Description</h3>
                  <p>{selectedScheme.description}</p>
                </div>

                <div className="scheme-info-section">
                  <h3>Benefits</h3>
                  <p>{selectedScheme.benefits}</p>
                </div>

                <div className="scheme-info-section">
                  <h3>Application Process</h3>
                  <p>{selectedScheme.applicationProcess}</p>
                </div>

                {selectedScheme.documents && selectedScheme.documents.length > 0 && (
                  <div className="scheme-info-section">
                    <h3>Required Documents</h3>
                    <ul className="documents-list">
                      {selectedScheme.documents.map((doc, index) => (
                        <li key={index}>
                          <FileText className="doc-icon" />
                          {doc}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                <div className="modal-actions">
                  {selectedScheme.videoLink && (
                    <a
                      href={selectedScheme.videoLink}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="video-btn"
                    >
                      <Play className="video-icon" />
                      Watch Tutorial
                    </a>
                  )}
                  
                  {selectedScheme.applicationLink && (
                    <a
                      href={selectedScheme.applicationLink}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="apply-btn"
                    >
                      <ExternalLink className="external-icon" />
                      Apply Now
                    </a>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SchemeMatcher;
