import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useLanguage } from '../context/LanguageContext';
import { useAuth } from '../context/AuthContext';
import { Search, TrendingUp, Shield, Users } from 'lucide-react';

const Hero = () => {
  const { t } = useLanguage();
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = () => {
    // Handle search functionality
    console.log('Searching for:', searchQuery);
  };

  const handleGetStarted = () => {
    if (currentUser) {
      navigate('/schemes');
    } else {
      navigate('/login');
    }
  };

  const handleExploreSchemes = () => {
    if (currentUser) {
      navigate('/schemes');
    } else {
      navigate('/login');
    }
  };

  return (
    <section className="hero">
      <div className="hero-container">
        <div className="hero-content">
          {/* Main headline */}
          <h1 className="hero-title">
            {t('heroTitle')} <span className="government">{t('government')}</span> <span className="benefits">{t('benefits')}</span>
          </h1>

          {/* Description */}
          <p className="hero-description">
            {t('heroDescription')}
          </p>

          {/* Search bar */}
          <div className="search-container">
            <input
              type="text"
              className="search-input"
              placeholder={t('searchPlaceholder')}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            />
            <button className="search-btn" onClick={handleSearch}>
              <Search className="search-icon" />
              {t('search')}
            </button>
          </div>

          {/* Action buttons */}
          <div className="hero-actions">
            <button className="explore-btn">{t('exploreSchemes')}</button>
            <button className="news-btn">{t('latestNews')}</button>
          </div>
        </div>

        {/* Illustration and stats */}
        <div className="hero-illustration">
          {/* Main illustration */}
          <div className="people-illustration">
            <div className="person person-1">
              <div className="person-head"></div>
              <div className="person-body orange"></div>
            </div>
            <div className="person person-2">
              <div className="person-head"></div>
              <div className="person-body blue">
                <div className="folder"></div>
              </div>
            </div>
            <div className="person person-3">
              <div className="person-head"></div>
              <div className="person-body teal"></div>
            </div>
          </div>

          {/* Floating stats cards */}
          <div className="stats-card stats-1">
            <TrendingUp className="stats-icon" />
            <span>{t('successRate')}</span>
          </div>
          <div className="stats-card stats-2">
            <Shield className="stats-icon" />
            <span>{t('verifiedAuthentic')}</span>
          </div>
          <div className="stats-card stats-3">
            <Users className="stats-icon" />
            <span>{t('monthlyUsers')}</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
