import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { CheckCircle, ExternalLink, Star, Calendar, DollarSign, FileText, Award, MapPin, User, Video, AlertCircle, Loader, Play } from 'lucide-react';

const SchemeMatcher = () => {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedScheme, setSelectedScheme] = useState(null);
  const { currentUser } = useAuth();

  useEffect(() => {
    loadSchemes();
  }, [currentUser]);

  const loadSchemes = async () => {
    try {
      setLoading(true);
      setError('');

      // Build URL with user ID for personalized schemes
      let url = `${process.env.REACT_APP_API_URL}/api/schemes?type=scheme`;
      if (currentUser?.id) {
        url += `&userId=${currentUser.id}`;
      }

      console.log('ðŸ” Fetching schemes from:', url);
      console.log('ðŸ‘¤ Current user:', currentUser);

      const response = await fetch(url);
      
      console.log('ðŸ“¡ Response status:', response.status);
      
      if (response.ok) {
        const data = await response.json();
        console.log('ðŸ“Š Received data:', data);
        console.log('ðŸ“ Schemes array:', data.schemes);
        console.log('ðŸ”¢ Number of schemes:', data.schemes?.length || 0);
        setSchemes(data.schemes || []);
      } else {
        throw new Error('Failed to load schemes');
      }
    } catch (error) {
      console.error('âŒ Error loading schemes:', error);
      setError('Unable to load schemes. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleSchemeClick = (scheme) => {
    setSelectedScheme(scheme);
  };

  const closeModal = () => {
    setSelectedScheme(null);
  };

  const formatEligibilityArray = (eligibility) => {
    if (Array.isArray(eligibility.category)) {
      return eligibility.category;
    }
    return [];
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Finding the best schemes for you...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto p-8">
          <AlertCircle className="w-16 h-16 text-red-500 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Unable to Load Schemes</h2>
          <p className="text-gray-600 mb-6">{error}</p>
          <button onClick={loadSchemes} className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-6xl mx-auto px-8">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">Government Schemes</h1>
          {schemes.length > 0 && currentUser?.profile ? (
            <p className="text-xl text-gray-600">Based on your profile, here are the schemes available for you</p>
          ) : (
            <p className="text-xl text-gray-600">Explore government schemes and benefits</p>
          )}
        </div>

        {schemes.length === 0 ? (
          <div className="text-center py-16">
            <Award className="w-24 h-24 text-gray-300 mx-auto mb-6" />
            <h3 className="text-2xl font-bold text-gray-900 mb-4">No Schemes Available</h3>
            <p className="text-gray-600 mb-2">There are currently no schemes in the database.</p>
            <p className="text-gray-600">Check back later or contact the administrator.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {schemes.map((scheme) => (
              <div key={scheme._id} className="bg-white rounded-2xl shadow-lg p-6 hover:shadow-xl transition-shadow cursor-pointer" onClick={() => handleSchemeClick(scheme)}>
                <div className="flex justify-between items-start mb-4">
                  <div className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-semibold">{scheme.type}</div>
                  {scheme.isActive && (
                    <div className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-sm font-semibold">Active</div>
                  )}
                </div>
                
                <h3 className="text-xl font-bold text-gray-900 mb-3">{scheme.title}</h3>
                <p className="text-gray-600 mb-4 leading-relaxed">{scheme.description}</p>
                
                <div className="flex items-center gap-2 mb-4 text-green-600">
                  <DollarSign className="w-5 h-5" />
                  <span className="font-semibold">{scheme.benefits}</span>
                </div>
                
                {scheme.lastDate && (
                  <div className="flex items-center gap-2 mb-6 text-gray-600">
                    <Calendar className="w-5 h-5" />
                    <span>Deadline: {new Date(scheme.lastDate).toLocaleDateString()}</span>
                  </div>
                )}
                
                <button className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition-colors">View Details</button>
              </div>
            ))}
          </div>
        )}

        {/* Scheme Detail Modal */}
        {selectedScheme && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50" onClick={closeModal}>
            <div className="bg-white rounded-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto" onClick={(e) => e.stopPropagation()}>
              <div className="flex justify-between items-center p-6 border-b">
                <h2 className="text-2xl font-bold text-gray-900">{selectedScheme.title}</h2>
                <button className="text-gray-500 hover:text-gray-700 text-2xl font-bold" onClick={closeModal}>Ã—</button>
              </div>
              
              <div className="p-6 space-y-6">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-3">Description</h3>
                  <p className="text-gray-600 leading-relaxed">{selectedScheme.description}</p>
                </div>

                <div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-3">Benefits</h3>
                  <p className="text-gray-600 leading-relaxed">{selectedScheme.benefits}</p>
                </div>

                <div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-3">Application Process</h3>
                  <p className="text-gray-600 leading-relaxed">{selectedScheme.applicationProcess}</p>
                </div>

                {selectedScheme.documents && selectedScheme.documents.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900 mb-3">Required Documents</h3>
                    <ul className="space-y-2">
                      {selectedScheme.documents.map((doc, index) => (
                        <li key={index} className="flex items-center gap-3 text-gray-600">
                          <FileText className="w-5 h-5 text-blue-500" />
                          {doc}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}

                <div className="flex gap-4 pt-6 border-t">
                  {selectedScheme.videoLink && (
                    <a
                      href={selectedScheme.videoLink}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 bg-purple-600 text-white px-6 py-3 rounded-lg hover:bg-purple-700 transition-colors"
                    >
                      <Play className="w-5 h-5" />
                      Watch Tutorial
                    </a>
                  )}
                  
                  {selectedScheme.applicationLink && (
                    <a
                      href={selectedScheme.applicationLink}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 transition-colors"
                    >
                      <ExternalLink className="w-5 h-5" />
                      Apply Now
                    </a>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SchemeMatcher;
