import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import './components.css';
import Header from './components/Header';
import Hero from './components/Hero';
import Features from './components/Features';
import Login from './components/Login';
import ProfileInput from './components/ProfileInput';
import SchemeMatcher from './components/SchemeMatcher';
import FakeNewsChecker from './components/FakeNewsChecker';
import ApplicationTracker from './components/ApplicationTracker';
import ScholarshipResources from './components/ScholarshipResources';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import { LanguageProvider } from './context/LanguageContext';
import { AuthProvider, useAuth } from './context/AuthContext';

// Protected Route component
const ProtectedRoute = ({ children }) => {
  const { currentUser, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }
  
  if (!currentUser) {
    return <Navigate to="/login" />;
  }
  
  return children;
};

// Public Route component (only for non-authenticated users)
const PublicRoute = ({ children }) => {
  const { currentUser, hasProfile, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }
  
  if (currentUser) {
    // If user is logged in but doesn't have a profile, redirect to profile setup
    if (!hasProfile()) {
      return <Navigate to="/profile" />;
    }
    // If user has a complete profile, redirect to home
    return <Navigate to="/" />;
  }
  
  return children;
};

function AppContent() {
  const { currentUser, hasProfile, loading } = useAuth();

  // Debug logging
  console.log('Auth State:', { 
    currentUser: !!currentUser, 
    hasProfile: hasProfile(), 
    loading 
  });

  return (
    <div className="App">
      {/* Development debug info */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{ 
          position: 'fixed', 
          top: '10px', 
          right: '10px', 
          background: 'rgba(0,0,0,0.8)', 
          color: 'white', 
          padding: '10px', 
          borderRadius: '5px', 
          fontSize: '12px',
          zIndex: 1000
        }}>
          Auth: {currentUser ? 'Logged In' : 'Not Logged In'}<br/>
          Profile: {hasProfile() ? 'Complete' : 'Incomplete'}<br/>
          Loading: {loading ? 'Yes' : 'No'}<br/>
          Email: {currentUser?.email || 'None'}
        </div>
      )}
      
      <Header />
      <Routes>
        <Route path="/" element={
          <>
            <Hero />
            <Features />
          </>
        } />
        <Route path="/login" element={
          <PublicRoute>
            <Login />
          </PublicRoute>
        } />
        <Route path="/profile" element={
          <ProtectedRoute>
            <ProfileInput />
          </ProtectedRoute>
        } />
        <Route path="/schemes" element={
          <ProtectedRoute>
            <SchemeMatcher />
          </ProtectedRoute>
        } />
        <Route path="/fake-news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/tracker" element={
          <ProtectedRoute>
            <ApplicationTracker />
          </ProtectedRoute>
        } />
        <Route path="/scholarships" element={
          <ProtectedRoute>
            <ScholarshipResources />
          </ProtectedRoute>
        } />
        <Route path="/news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/about" element={
          <div className="about-page container" style={{ padding: '60px 20px', maxWidth: '800px', margin: '0 auto', lineHeight: '1.6' }}>
            <h1 style={{ textAlign: 'center', marginBottom: '30px', color: '#333' }}>About Smart Sarkari Sathi</h1>
            
            <div style={{ fontSize: '16px', color: '#555', textAlign: 'left' }}>
              <p style={{ marginBottom: '20px' }}>
                Smart Sarkari Sathi is an AI-powered digital assistant designed to simplify access to government schemes, services, and information for every citizen of India. We aim to bridge the gap between people and the often complex, scattered world of public services by offering a single, easy-to-use platform.
              </p>

              <h3 style={{ color: '#333', marginTop: '30px', marginBottom: '15px' }}>With Smart Sarkari Sathi, users can:</h3>
              
              <ul style={{ marginBottom: '25px', paddingLeft: '20px' }}>
                <li style={{ marginBottom: '8px' }}><strong>Search & Discover</strong> government schemes relevant to their profile, location, and eligibility.</li>
                <li style={{ marginBottom: '8px' }}><strong>Get Personalized Recommendations</strong> based on age, profession, income, and other factors.</li>
                <li style={{ marginBottom: '8px' }}><strong>Track Applications & Updates</strong> in real time.</li>
                <li style={{ marginBottom: '8px' }}><strong>Access Multilingual Support</strong> to ensure inclusivity across India's diverse population.</li>
                <li style={{ marginBottom: '8px' }}><strong>Use Voice & Chat Assistance</strong> for hands-free navigation and query resolution.</li>
              </ul>

              <p style={{ marginBottom: '20px' }}>
