import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import './components.css';
import Header from './components/Header';
import Hero from './components/Hero';
import Features from './components/Features';
import Login from './components/Login';
import ProfileInput from './components/ProfileInput';
import SchemeMatcher from './components/SchemeMatcher';
import FakeNewsChecker from './components/FakeNewsChecker';
import ApplicationTracker from './components/ApplicationTracker';
import ScholarshipResources from './components/ScholarshipResources';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import { LanguageProvider } from './context/LanguageContext';
import { AuthProvider, useAuth } from './context/AuthContext';

// Protected Route component
const ProtectedRoute = ({ children }) => {
  const { currentUser, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }
  
  if (!currentUser) {
    return <Navigate to="/login" />;
  }
  
  return children;
};

// Public Route component (only for non-authenticated users)
const PublicRoute = ({ children }) => {
  const { currentUser, hasProfile, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }
  
  if (currentUser) {
    // If user is logged in but doesn't have a profile, redirect to profile setup
    if (!hasProfile()) {
      return <Navigate to="/profile" />;
    }
    // If user has a complete profile, redirect to home
    return <Navigate to="/" />;
  }
  
  return children;
};

function AppContent() {
  const { currentUser, hasProfile, loading } = useAuth();

  // Debug logging
  console.log('Auth State:', { 
    currentUser: !!currentUser, 
    hasProfile: hasProfile(), 
    loading 
  });

  return (
    <div className="App">
      {/* Development debug info */}
      {process.env.NODE_ENV === 'development' && (
        <div style={{ 
          position: 'fixed', 
          top: '10px', 
          right: '10px', 
          background: 'rgba(0,0,0,0.8)', 
          color: 'white', 
          padding: '10px', 
          borderRadius: '5px', 
          fontSize: '12px',
          zIndex: 1000
        }}>
          Auth: {currentUser ? 'Logged In' : 'Not Logged In'}<br/>
          Profile: {hasProfile() ? 'Complete' : 'Incomplete'}<br/>
          Loading: {loading ? 'Yes' : 'No'}<br/>
          Email: {currentUser?.email || 'None'}
        </div>
      )}
      
      <Header />
      <Routes>
        <Route path="/" element={
          <>
            <Hero />
            <Features />
          </>
        } />
        <Route path="/login" element={
          <PublicRoute>
            <Login />
          </PublicRoute>
        } />
        <Route path="/profile" element={
          <ProtectedRoute>
            <ProfileInput />
          </ProtectedRoute>
        } />
        <Route path="/schemes" element={
          <ProtectedRoute>
            <SchemeMatcher />
          </ProtectedRoute>
        } />
        <Route path="/fake-news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/tracker" element={
          <ProtectedRoute>
            <ApplicationTracker />
          </ProtectedRoute>
        } />
        <Route path="/scholarships" element={
          <ProtectedRoute>
            <ScholarshipResources />
          </ProtectedRoute>
        } />
        <Route path="/news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/about" element={
          <div className="about-page container" style={{ padding: '60px 20px', textAlign: 'center' }}>
            <h1>About Smart Sarkari Sathi</h1>
            <p style={{ fontSize: '18px', marginTop: '20px', color: '#666' }}>
              Your intelligent guide to government schemes and scholarships.<br/>
              Built with MongoDB, Express.js, React, and Node.js.
            </p>
          </div>
        } />
        {/* Catch all route */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </div>
  );
}

function App() {
  return (
    <LanguageProvider>
      <AuthProvider>
        <Router>
          <AppContent />
        </Router>
      </AuthProvider>
    </LanguageProvider>
  );
}

export default App;
