import React from 'react';
import { Link } from 'react-router-dom';
import { useLanguage } from '../context/LanguageContext';
import { useAuth } from '../context/AuthContext';
import { Flower, Sun, LogOut, Shield } from 'lucide-react';

const Header = () => {
  const { t, language, toggleLanguage } = useLanguage();
  const { currentUser, logout, loading } = useAuth();

  return (
    <header className="bg-white shadow-md py-4 px-6 flex items-center justify-between">
      <div className="flex items-center space-x-2">
        <Flower className="w-8 h-8 text-purple-600" />
        <span className="text-xl font-bold text-gray-800">Sevika</span>
      </div>
      <nav className="flex space-x-4">
        <Link to="/" className="text-gray-700 hover:text-purple-600 font-medium">{t('home')}</Link>
        {currentUser ? (
          <>
            <Link to="/schemes" className="text-gray-700 hover:text-purple-600 font-medium">{t('schemes')}</Link>
            <Link to="/scholarships" className="text-gray-700 hover:text-purple-600 font-medium">{t('scholarships')}</Link>
            <Link to="/news" className="text-gray-700 hover:text-purple-600 font-medium">{t('news')}</Link>
          </>
        ) : (
          <a href="#features" className="text-gray-700 hover:text-purple-600 font-medium">Features</a>
        )}
        <Link to="/about" className="text-gray-700 hover:text-purple-600 font-medium">{t('about')}</Link>
      </nav>
      <div className="flex items-center space-x-2">
        <button 
          className="px-3 py-1 rounded bg-gray-100 hover:bg-purple-100 text-sm font-semibold"
          onClick={toggleLanguage}
          title={language === 'english' ? 'हिंदी में बदलें' : 'Switch to English'}
        >
          {language === 'english' ? 'हिंदी' : 'ENG'}
        </button>
        <button className="px-2 py-1 rounded bg-gray-100 hover:bg-yellow-100" title="Toggle theme">
          <Sun className="w-5 h-5 text-yellow-500" />
        </button>
        {currentUser ? (
          <button onClick={logout} className="flex items-center px-3 py-1 rounded bg-red-500 text-white hover:bg-red-600 text-sm font-semibold">
            <LogOut size={16} className="mr-1" />
            {t('logout')}
          </button>
        ) : (
          <div className="flex space-x-2">
            <Link to="/admin/login" className="flex items-center px-3 py-1 rounded bg-gray-200 hover:bg-gray-300 text-sm font-semibold">
              <Shield size={16} className="mr-1" />
              Admin
            </Link>
            <Link to="/login" className="px-3 py-1 rounded bg-purple-600 text-white hover:bg-purple-700 text-sm font-semibold">
              {t('getStarted')}
            </Link>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
