import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useLanguage } from '../context/LanguageContext';
import { useAuth } from '../context/AuthContext';
import { Search, TrendingUp, Shield, Users } from 'lucide-react';

const Hero = () => {
  const { t } = useLanguage();
  const { currentUser } = useAuth();
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = () => {
    // Handle search functionality
    console.log('Searching for:', searchQuery);
  };

  const handleGetStarted = () => {
    if (currentUser) {
      navigate('/schemes');
    } else {
      navigate('/login');
    }
  };

  const handleExploreSchemes = () => {
    if (currentUser) {
      navigate('/schemes');
    } else {
      navigate('/login');
    }
  };

  return (
    <section className="bg-white py-16">
      <div className="max-w-6xl mx-auto px-8 grid grid-cols-1 lg:grid-cols-2 gap-16 items-center">
        <div className="space-y-8">
          {/* Main headline */}
          <h1 className="text-5xl font-bold leading-tight">
            {t('heroTitle')} <span className="text-pink-500">{t('government')}</span> <span className="text-purple-600">{t('benefits')}</span>
          </h1>

          {/* Description */}
          <p className="text-xl text-gray-600 leading-relaxed">
            {t('heroDescription')}
          </p>

          {/* Search bar */}
          <div className="flex gap-2">
            <input
              type="text"
              className="flex-1 px-4 py-3 border-2 border-gray-200 rounded-full text-lg outline-none focus:border-pink-500 transition-colors"
              placeholder={t('searchPlaceholder')}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            />
            <button className="bg-gradient-to-r from-pink-500 to-purple-600 text-white px-8 py-3 rounded-full font-semibold flex items-center gap-2 hover:transform hover:-translate-y-1 transition-transform" onClick={handleSearch}>
              <Search className="w-5 h-5" />
              {t('search')}
            </button>
          </div>

          {/* Action buttons */}
          <div className="flex gap-4">
            <button className="bg-gradient-to-r from-pink-500 to-purple-600 text-white px-8 py-3 rounded-full font-semibold hover:transform hover:-translate-y-1 transition-transform" onClick={handleExploreSchemes}>
              {currentUser ? t('exploreSchemes') : 'Get Started'}
            </button>
            <button className="bg-white text-pink-500 border-2 border-pink-500 px-8 py-3 rounded-full font-semibold hover:bg-pink-500 hover:text-white transition-all" onClick={() => navigate('/about')}>
              {t('latestNews') || 'Learn More'}
            </button>
          </div>

          {/* Authentication status message */}
          {!currentUser && (
            <p className="text-center text-gray-600 text-lg">
              üîê <strong>{t('loginToAccess')}</strong>
            </p>
          )}
        </div>

        {/* Illustration and stats */}
        <div className="relative h-96">
          {/* Main illustration */}
          <div className="flex justify-center items-end gap-4 h-72">
            <div className="flex flex-col items-center">
              <div className="w-16 h-16 bg-yellow-400 rounded-full mb-2"></div>
              <div className="w-20 h-32 bg-pink-500 rounded-t-3xl rounded-b-lg"></div>
            </div>
            <div className="flex flex-col items-center">
              <div className="w-16 h-16 bg-yellow-400 rounded-full mb-2"></div>
              <div className="w-20 h-32 bg-teal-400 rounded-t-3xl rounded-b-lg relative">
                <div className="absolute top-5 left-1/2 transform -translate-x-1/2 w-10 h-8 bg-red-500 rounded"></div>
              </div>
            </div>
            <div className="flex flex-col items-center">
              <div className="w-16 h-16 bg-yellow-400 rounded-full mb-2"></div>
              <div className="w-20 h-32 bg-blue-400 rounded-t-3xl rounded-b-lg"></div>
            </div>
          </div>

          {/* Floating stats cards */}
          <div className="absolute top-5 left-5 bg-white p-4 rounded-2xl shadow-lg flex items-center gap-2 font-semibold text-sm">
            <TrendingUp className="w-5 h-5 text-teal-500" />
            <span>{t('successRate')}</span>
          </div>
          <div className="absolute bottom-20 left-5 bg-white p-4 rounded-2xl shadow-lg flex items-center gap-2 font-semibold text-sm">
            <Shield className="w-5 h-5 text-teal-500" />
            <span>{t('verifiedAuthentic')}</span>
          </div>
          <div className="absolute top-12 right-5 bg-white p-4 rounded-2xl shadow-lg flex items-center gap-2 font-semibold text-sm">
            <Users className="w-5 h-5 text-teal-500" />
            <span>{t('monthlyUsers')}</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
