import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Hero from './components/Hero';
import Features from './components/Features';
import Login from './components/Login';
import ProfileInput from './components/ProfileInput';
import SchemeMatcher from './components/SchemeMatcher';
import FakeNewsChecker from './components/FakeNewsChecker';
import ScholarshipResources from './components/ScholarshipResources';
import About from './components/About';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import { LanguageProvider, useLanguage } from './context/LanguageContext';
import { AuthProvider, useAuth } from './context/AuthContext';

// Protected Route component
const ProtectedRoute = ({ children }) => {
  const { currentUser, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }
  
  if (!currentUser) {
    return <Navigate to="/login" />;
  }
  
  return children;
};

// Public Route component (only for non-authenticated users)
const PublicRoute = ({ children }) => {
  const { currentUser, hasProfile, loading } = useAuth();
  
  // Show loading while auth state is being determined
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }
  
  if (currentUser) {
    // If user is logged in but doesn't have a profile, redirect to profile setup
    if (!hasProfile()) {
      return <Navigate to="/profile" />;
    }
    // If user has a complete profile, redirect to home
    return <Navigate to="/" />;
  }
  
  return children;
};

function AppContent() {
  const { currentUser, hasProfile, loading } = useAuth();

  return (
    <div className="App">
      <Header />
      <Routes>
        <Route path="/" element={
          <>
            <Hero />
            <Features />
          </>
        } />
        <Route path="/login" element={
          <PublicRoute>
            <Login />
          </PublicRoute>
        } />
        <Route path="/profile" element={
          <ProtectedRoute>
            <ProfileInput />
          </ProtectedRoute>
        } />
        <Route path="/schemes" element={
          <ProtectedRoute>
            <SchemeMatcher />
          </ProtectedRoute>
        } />
        <Route path="/fake-news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/scholarships" element={
          <ProtectedRoute>
            <ScholarshipResources />
          </ProtectedRoute>
        } />
        <Route path="/news" element={
          <ProtectedRoute>
            <FakeNewsChecker />
          </ProtectedRoute>
        } />
        <Route path="/about" element={
          <div className="about-page" style={{ 
            backgroundColor: '#f8f9fa', 
            minHeight: '100vh', 
            paddingTop: '80px',
            paddingBottom: '60px'
          }}>
            <div className="container" style={{ 
              maxWidth: '1200px', 
              margin: '0 auto', 
              padding: '0 20px' 
            }}>
              
              {/* Header Section */}
              <div style={{ 
                textAlign: 'center', 
                marginBottom: '60px',
                backgroundColor: 'white',
                padding: '60px 40px',
                borderRadius: '12px',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
              }}>
                <h1 style={{ 
                  fontSize: '2.5rem', 
                  fontWeight: '700', 
