import React from 'react';
import { useLanguage } from '../context/LanguageContext';

const About = () => {
  const { t } = useLanguage();

  return (
    <div className="about-page" style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', 
      padding: '80px 20px 40px'
    }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        {/* Header Section */}
        <div style={{ 
          textAlign: 'center', 
          marginBottom: '60px',
          backgroundColor: 'white',
          padding: '60px 40px',
          borderRadius: '12px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
        }}>
          <h1 style={{ 
            fontSize: '2.5rem', 
            fontWeight: '700', 
            color: '#2d3748', 
            marginBottom: '20px' 
          }}>{t('aboutTitle')}</h1>
          <p style={{ 
            fontSize: '1.2rem', 
            color: '#4a5568', 
            lineHeight: '1.6',
            maxWidth: '800px',
            margin: '0 auto'
          }}>
            {t('aboutDescription')}
          </p>
        </div>

        {/* Mission Section */}
        <div style={{
          backgroundColor: 'white',
          padding: '50px 40px',
          borderRadius: '12px',
          marginBottom: '30px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
        }}>
          <h2 style={{ 
            fontSize: '1.8rem', 
            fontWeight: '600', 
            color: '#2d3748', 
            marginBottom: '20px' 
          }}>{t('ourMission')}</h2>
          <p style={{ 
            fontSize: '1.1rem', 
            color: '#4a5568', 
            lineHeight: '1.7',
            marginBottom: '0'
          }}>
            {t('missionDescription')}
          </p>
        </div>

        {/* Features Section */}
        <div style={{
          backgroundColor: 'white',
          padding: '50px 40px',
          borderRadius: '12px',
          marginBottom: '30px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
        }}>
          <h2 style={{ 
            fontSize: '1.8rem', 
            fontWeight: '600', 
            color: '#2d3748', 
            marginBottom: '30px' 
          }}>{t('whatWeOffer')}</h2>
          
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
            gap: '25px' 
          }}>
            <div style={{ padding: '20px 0' }}>
              <h3 style={{ 
                fontSize: '1.2rem', 
                fontWeight: '600', 
                color: '#c44569', 
                marginBottom: '10px' 
              }}>üîç {t('searchDiscover')}</h3>
              <p style={{ color: '#4a5568', lineHeight: '1.6' }}>
                {t('searchDescription')}
              </p>
            </div>
            
            <div style={{ padding: '20px 0' }}>
              <h3 style={{ 
                fontSize: '1.2rem', 
                fontWeight: '600', 
                color: '#c44569', 
                marginBottom: '10px' 
              }}>üéØ {t('personalizedRecommendations')}</h3>
              <p style={{ color: '#4a5568', lineHeight: '1.6' }}>
                {t('personalizedDescription')}
              </p>
            </div>
            
            <div style={{ padding: '20px 0' }}>
              <h3 style={{ 
                fontSize: '1.2rem', 
                fontWeight: '600', 
                color: '#c44569', 
                marginBottom: '10px' 
              }}>üåê {t('multilingualSupport')}</h3>
              <p style={{ color: '#4a5568', lineHeight: '1.6' }}>
                {t('multilingualDescription')}
              </p>
            </div>
            
            <div style={{ padding: '20px 0' }}>
              <h3 style={{ 
                fontSize: '1.2rem', 
                fontWeight: '600', 
                color: '#c44569', 
                marginBottom: '10px' 
              }}>ü§ñ {t('aiPowered')}</h3>
              <p style={{ color: '#4a5568', lineHeight: '1.6' }}>
                {t('aiDescription')}
              </p>
            </div>
          </div>
        </div>

        {/* Vision Section */}
        <div style={{
          backgroundColor: 'white',
          padding: '50px 40px',
          borderRadius: '12px',
          marginBottom: '30px',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)'
        }}>
          <h2 style={{ 
            fontSize: '1.8rem', 
            fontWeight: '600', 
            color: '#2d3748', 
            marginBottom: '20px' 
          }}>{t('ourVision')}</h2>
          <p style={{ 
            fontSize: '1.1rem', 
            color: '#4a5568', 
            lineHeight: '1.7',
            marginBottom: '20px'
          }}>
            {t('visionDescription1')}
          </p>
          <p style={{ 
            fontSize: '1.1rem', 
            color: '#2d3748', 
            lineHeight: '1.7',
            fontWeight: '600'
          }}>
            {t('visionDescription2')}
          </p>
        </div>
      </div>
    </div>
  );
};

export default About;
