import React from 'react';
import { Link } from 'react-router-dom';
import { useLanguage } from '../context/LanguageContext';
import { useAuth } from '../context/AuthContext';
import { Flower, Sun, LogOut, Shield } from 'lucide-react';

const Header = () => {
  const { t, language, toggleLanguage } = useLanguage();
  const { currentUser, logout, loading } = useAuth();

  // Debug info
  console.log('Header Auth State:', { currentUser: !!currentUser, loading });

  return (
    <header className="header">
      <div className="header-container">
        {/* Logo */}
        <div className="logo">
          <Flower className="logo-icon" />
          <span className="logo-text">Sevika</span>
        </div>

        {/* Navigation */}
        <nav className="nav">
          <Link to="/" className="nav-link">{t('home')}</Link>
          {currentUser ? (
            <>
              <Link to="/schemes" className="nav-link">{t('schemes')}</Link>
              <Link to="/scholarships" className="nav-link">{t('scholarships')}</Link>
              <Link to="/news" className="nav-link">{t('news')}</Link>
              
            </>
          ) : (
            <>
              <a href="#features" className="nav-link">Features</a>
              
            </>
          )}
          <Link to="/about" className="nav-link">{t('about')}</Link>
        </nav>

        {/* Right side controls */}
        <div className="header-controls">
          {/* Language toggle */}
          <button 
            className="language-toggle"
            onClick={toggleLanguage}
            title={language === 'english' ? 'हिंदी में बदलें' : 'Switch to English'}
          >
            {language === 'english' ? 'हिंदी' : 'ENG'}
          </button>

          {/* Theme toggle */}
          <button className="theme-toggle" title="Toggle theme">
            <Sun className="theme-icon" />
          </button>

          {/* Get Started button or Logout */}
          {currentUser ? (
            <button onClick={logout} className="logout-btn">
              <LogOut size={16} />
              {t('logout')}
            </button>
          ) : (
            <div className="auth-buttons">
              <Link to="/admin/login" className="admin-btn">
                <Shield size={16} />
                Admin
              </Link>
              <Link to="/login" className="get-started-btn">
                {t('getStarted')}
              </Link>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
