import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { GraduationCap, FileText, Download, ExternalLink, Search, Filter, BookOpen, Award, Users, Calendar, AlertCircle, Loader, Play } from 'lucide-react';

const ScholarshipResources = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [scholarships, setScholarships] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedScholarship, setSelectedScholarship] = useState(null);
  const { currentUser } = useAuth();

  useEffect(() => {
    loadScholarships();
  }, [currentUser]);

  const loadScholarships = async () => {
    try {
      setLoading(true);
      setError('');

      // Build URL for scholarships (type=scholarship)
      let url = 'http://localhost:5000/api/schemes?type=scholarship';
      if (currentUser?.id) {
        url += `&userId=${currentUser.id}`;
      }

      const response = await fetch(url);
      
      if (response.ok) {
        const data = await response.json();
        setScholarships(data.schemes || []);
      } else {
        throw new Error('Failed to load scholarships');
      }
    } catch (error) {
      console.error('Error loading scholarships:', error);
      setError('Unable to load scholarships. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const categories = [
    { value: 'all', label: 'All Categories' },
    { value: 'General', label: 'General' },
    { value: 'SC/ST', label: 'SC/ST' },
    { value: 'OBC', label: 'OBC' },
    { value: 'Minority', label: 'Minority' },
    { value: 'Defense', label: 'Defense' },
    { value: 'Technical', label: 'Technical' }
  ];

  const filteredScholarships = scholarships.filter(scholarship => {
    const matchesSearch = scholarship.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         scholarship.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === 'all' || 
                           scholarship.category === selectedCategory ||
                           scholarship.eligibility?.category?.includes(selectedCategory);
    return matchesSearch && matchesCategory;
  });

  const handleScholarshipClick = (scholarship) => {
    setSelectedScholarship(scholarship);
  };

  const closeModal = () => {
    setSelectedScholarship(null);
  };

  if (loading) {
    return (
      <div className="scholarship-resources">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading scholarships...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="scholarship-resources">
        <div className="error-container">
          <AlertCircle className="error-icon" />
          <h2>Unable to Load Scholarships</h2>
          <p>{error}</p>
          <button onClick={loadScholarships} className="retry-btn">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="scholarship-resources">
      <div className="scholarship-header">
        <div className="header-content">
          <div className="header-text">
            <h1>
              <GraduationCap className="header-icon" />
              Scholarship Resources
            </h1>
            <p>Discover educational opportunities and financial assistance programs</p>
          </div>
        </div>
      </div>

      <div className="search-filter-section">
        <div className="search-container">
          <Search className="search-icon" />
          <input
            type="text"
            placeholder="Search scholarships..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
        </div>

        <div className="filter-container">
          <Filter className="filter-icon" />
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="filter-select"
          >
            {categories.map(category => (
              <option key={category.value} value={category.value}>
                {category.label}
              </option>
            ))}
          </select>
        </div>
      </div>

      {filteredScholarships.length === 0 ? (
        <div className="no-scholarships">
          <BookOpen className="no-scholarships-icon" />
          <h3>No Scholarships Found</h3>
          <p>
            {scholarships.length === 0 
              ? "There are currently no scholarships in the database."
              : "No scholarships match your search criteria. Try adjusting your filters."
            }
          </p>
        </div>
      ) : (
        <div className="scholarships-grid">
          {filteredScholarships.map((scholarship) => (
            <div key={scholarship._id} className="scholarship-card" onClick={() => handleScholarshipClick(scholarship)}>
              <div className="scholarship-header-card">
                <div className="scholarship-category">{scholarship.category || 'General'}</div>
                {scholarship.isActive && (
                  <div className="scholarship-status active">Active</div>
                )}
              </div>
              
              <h3 className="scholarship-name">{scholarship.title}</h3>
              <p className="scholarship-description">{scholarship.description}</p>
              
              <div className="scholarship-details">
                <div className="scholarship-amount">
                  <Award className="amount-icon" />
                  <span>{scholarship.benefits}</span>
                </div>
                
                {scholarship.lastDate && (
                  <div className="scholarship-deadline">
                    <Calendar className="deadline-icon" />
                    <span>Deadline: {new Date(scholarship.lastDate).toLocaleDateString()}</span>
                  </div>
                )}
              </div>
              
              <button className="view-details-btn">View Details</button>
            </div>
          ))}
        </div>
      )}

      {/* Scholarship Detail Modal */}
      {selectedScholarship && (
        <div className="scholarship-modal-overlay" onClick={closeModal}>
          <div className="scholarship-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{selectedScholarship.title}</h2>
              <button className="close-modal" onClick={closeModal}>Ã—</button>
            </div>
            
            <div className="modal-content">
              <div className="scholarship-info-section">
                <h3>Description</h3>
                <p>{selectedScholarship.description}</p>
              </div>

              <div className="scholarship-info-section">
                <h3>Benefits</h3>
                <p>{selectedScholarship.benefits}</p>
              </div>

              <div className="scholarship-info-section">
                <h3>Application Process</h3>
                <p>{selectedScholarship.applicationProcess}</p>
              </div>

              {selectedScholarship.documents && selectedScholarship.documents.length > 0 && (
                <div className="scholarship-info-section">
                  <h3>Required Documents</h3>
                  <ul className="documents-list">
                    {selectedScholarship.documents.map((doc, index) => (
                      <li key={index}>
                        <FileText className="doc-icon" />
                        {doc}
                      </li>
                    ))}
                  </ul>
                </div>
              )}

              <div className="modal-actions">
                {selectedScholarship.videoLink && (
                  <a
                    href={selectedScholarship.videoLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="video-btn"
                  >
                    <Play className="video-icon" />
                    Watch Tutorial
                  </a>
                )}
                
                {selectedScholarship.applicationLink && (
                  <a
                    href={selectedScholarship.applicationLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="apply-btn"
                  >
                    <ExternalLink className="external-icon" />
                    Apply Now
                  </a>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ScholarshipResources;
