import React from 'react';
import { Link } from 'react-router-dom';
import { useLanguage } from '../context/LanguageContext';
import { Menu, AlertTriangle, MapPin, GraduationCap } from 'lucide-react';

const Features = () => {
  const { t } = useLanguage();

  const features = [
    {
      id: 'scheme-recommender',
      icon: <Menu className="feature-icon" />,
      title: t('schemeRecommender'),
      description: t('schemeRecommenderDesc'),
      link: '/schemes',
      color: 'blue'
    },
    {
      id: 'fake-news-checker',
      icon: <AlertTriangle className="feature-icon" />,
      title: t('fakeNewsChecker'),
      description: t('fakeNewsCheckerDesc'),
      link: '/fake-news',
      color: 'orange'
    },
    
    {
      id: 'scholarship-resources',
      icon: <GraduationCap className="feature-icon" />,
      title: t('scholarshipResources'),
      description: t('scholarshipResourcesDesc'),
      link: '/scholarships',
      color: 'purple'
    }
  ];

  return (
    <section className="bg-gradient-to-br from-yellow-200 to-orange-200 py-16" id="features">
      <div className="max-w-6xl mx-auto px-8">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {features.map((feature) => (
            <Link to={feature.link} key={feature.id} className="bg-white p-8 rounded-3xl shadow-lg hover:transform hover:-translate-y-3 transition-all duration-300 text-decoration-none text-inherit">
              <div className={`w-15 h-15 rounded-2xl flex items-center justify-center mb-6 ${
                feature.color === 'blue' ? 'bg-gradient-to-br from-blue-400 to-blue-600' :
                feature.color === 'orange' ? 'bg-gradient-to-br from-pink-400 to-pink-600' :
                'bg-gradient-to-br from-purple-400 to-purple-600'
              }`}>
                <div className="w-8 h-8 text-white">
                  {feature.icon}
                </div>
              </div>
              <h3 className="text-xl font-bold mb-4 text-gray-800">{feature.title}</h3>
              <p className="text-gray-600 leading-relaxed">{feature.description}</p>
            </Link>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Features;
