import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useLanguage } from '../context/LanguageContext';
import { Mail, CheckCircle, AlertCircle, ArrowLeft, Key } from 'lucide-react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState('');

  const { sendOTP, verifyOTP } = useAuth();
  const { t } = useLanguage();

  const handleSendOTP = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      await sendOTP(email);
      setOtpSent(true);
      setSuccess(t('otpSent'));
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      await verifyOTP(email, otp);
      setSuccess(t('loginSuccess'));
      // User will be automatically redirected by AuthContext
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleResendOTP = async () => {
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      await sendOTP(email);
      setSuccess(t('otpResent'));
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleBackToEmail = () => {
    setOtpSent(false);
    setOtp('');
    setError('');
    setSuccess('');
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <h2>{t('login')}</h2>
          <p>{otpSent ? t('enterOTP') : t('loginSubtitle')}</p>
        </div>

        {error && (
          <div className="error-message">
            <AlertCircle size={16} />
            <span>{error}</span>
          </div>
        )}

        {success && (
          <div className="success-message">
            <CheckCircle size={16} />
            <span>{success}</span>
          </div>
        )}

        {!otpSent ? (
          <form onSubmit={handleSendOTP} className="login-form">
            <div className="form-group">
              <label htmlFor="email">{t('email')}</label>
              <div className="input-wrapper">
                <Mail size={18} className="input-icon" />
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder={t('enterEmail')}
                  required
                  disabled={loading}
                />
              </div>
              <small className="input-hint">{t('emailHint')}</small>
            </div>

            <button type="submit" className="submit-btn" disabled={loading}>
              {loading ? t('sending') : t('sendOTP')}
            </button>
          </form>
        ) : (
          <form onSubmit={handleVerifyOTP} className="login-form">
            <div className="otp-info">
              <CheckCircle size={48} className="otp-icon" />
              <h3>{t('enterOTP')}</h3>
              <p>{t('otpSentTo', { email })}</p>
            </div>

            <div className="form-group">
              <label htmlFor="otp">{t('otpCode')}</label>
              <div className="otp-input-wrapper">
                <Key size={18} className="input-icon" />
                <input
                  type="text"
                  id="otp"
                  value={otp}
                  onChange={(e) => {
                    const value = e.target.value.replace(/\D/g, '').slice(0, 6);
                    setOtp(value);
                  }}
                  placeholder={t('enterOTPCode')}
                  maxLength="6"
                  required
                  disabled={loading}
                  className="otp-input"
                />
              </div>
              <small className="input-hint">{t('otpHint')}</small>
            </div>

            <button type="submit" className="submit-btn" disabled={loading || otp.length < 6}>
              {loading ? t('verifying') : t('verifyOTP')}
            </button>

            <div className="otp-actions">
              <button 
                type="button"
                onClick={handleResendOTP} 
                className="resend-btn" 
                disabled={loading}
              >
                {loading ? t('sending') : t('resendOTP')}
              </button>
              
              <button 
                type="button"
                onClick={handleBackToEmail} 
                className="back-btn"
                disabled={loading}
              >
                <ArrowLeft size={16} />
                {t('changeEmail')}
              </button>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default Login;
